import Layout from "../../components/Layout"
import { useRouter } from 'next/router'
import posts from '../../static/topic.json'
import Link from 'next/link'

const OtherTopics = (props) => {
    const otherTopics = props.posts.filter(post => post.postName != props.currPostName);
    return (
        <div>
            {
                otherTopics.map(topic => (
                    <div class="timelineContainer pt-5"> 
                    <Link href="/posts/[postName]" as={`/posts/${topic.postName}`}>
                        <a><img class="img-fluid" src="../../static/assets/template/images/t1-sm.png"/></a>
                        <p class="textBelow colorWhite fontSize1vw">{topic.name}</p>
                    </Link>

                </div>
                ))

            }
        </div>
    );
}

const Post = () => {
    const router = useRouter();
    const currPostName = router.query.postName;
    const currName = posts.filter(post => post.postName == currPostName)

    // how do I get the name of this post name?
    return (
        <Layout>
            <h3 class="colorDarkBlue fontSansation fontSize1-5vw pl-5 ml-5">{currName.name}</h3>
            <div class="container-fluid m-4">
                <div class="row">            
                    <div class="col-md-1">
                    </div>
                    <div class="col-md-6 py-2">
                    </div>

                    {/* <!-- for other topics --> */}
                    <div class="col-md-4 vl colScroll">
                        <div class="timelineContainer">
                            <h3 class="fontSize1-5vw">CÁC CHỦ ĐỀ BÀI VIẾT</h3>
                        </div>
                        <OtherTopics posts={posts} currPostName={currPostName}/>
                    </div>
                </div>
            </div>
        </Layout>
    );
}
export default Post;
