import Layout from "../../components/Layout"
import { useRouter } from 'next/router'
import posts from '../../static/topic.json'
import postContent from '../../static/postContent.json'
import Link from 'next/link'

const OtherTopics = (props) => {
    const otherTopics = props.posts.filter(post => post.postName != props.currPostName);
    return (
        <div>
            {
                otherTopics.map(topic => (
                    <div className="timelineContainer pt-5">
                        <Link href="/posts/[postName]" as={`/posts/${topic.postName}`}> 
                            <a><img className="img-fluid" src="../../static/assets/template/images/t1-sm.png"/>
                                <p className="textBelow colorWhite fontSize1vw">{topic.name}</p>
                            </a>
                        </Link>
                    </div>
                ))
            }
        </div>
    );
}

const MainTopicArticle = (props) => {
    // filter out all the articles that belong to other topics
    console.log(props.postContent)
    const currTopicPost = props.postContent.filter(article => article.postName == props.currPostName);
    // filter out the 1st article
    console.log(currTopicPost)
    const otherArticles = currTopicPost.filter(article => article.id != currTopicPost[0].id);
    return (
        <div>
            <div>
                {/* <!-- illustration image --> */}
                <img class="img-fluid borderRect" src={'currTopicPost[0].image'}/>
                <a href="#"><h3 class="colorDarkBlue fontRoboBold fontSize1-5vw pt-3">HI</h3></a>
                {/* <!-- short excerpt of the article--> */}
                <p class="fontRoboLight fontSize1-2vw">THERE
                </p>
            </div>
            <hr/>
            {
                otherArticles.map(article => (
                    <div>
                        <div class="row pt-4">
                            <div class="col-md-3">
                                <img class="img-fluid" src={'article.image'}/>
                            </div>
                            <div class="col-md-8">
                                <a href="#"><h4 class="colorDarkBlue fontSize1-5vw fontRoboBold">{article.name}</h4></a>
                                <p class="fontSize1-2vw fontRoboLight">{article.excerpt}</p>
                            </div>
                        </div>
                        <hr/>
                    </div>    
                ))
            }
        </div>
    )
}

const Post = () => {
    const router = useRouter();
    const currPostName = router.query.postName;
    const currName = posts.filter(post => post.postName == currPostName)

    // how do I get the name of this post name?
    return (
        <Layout>
            <h3 className="colorDarkBlue fontSansation fontSize1-5vw pl-5 ml-5">{currName.name}</h3>
            <div className="container-fluid m-4">
                <div className="row">            
                    <div className="col-md-1">
                    </div>
                    <div className="col-md-6 py-2">
                        <MainTopicArticle postContent={postContent} currPostName={currPostName}/> 
                    </div>
                    {/* <!-- for other topics --> */}
                    <div className="col-md-4 vl colScroll">
                        <div className="timelineContainer">
                            <h3 className="fontSize1-5vw">CÁC CHỦ ĐỀ BÀI VIẾT</h3>
                        </div>
                        <OtherTopics posts={posts} currPostName={currPostName}/>
                    </div>
                </div>
            </div>
        </Layout>
    );
}
export default Post;
