import Layout from "../../components/Layout"
import { useRouter } from 'next/router'
import posts from '../../static/topic.json'

const OtherTopic = (props) => {
    const otherTopics = props.posts.filter(post => post.postName != props.currPostName);
    return (
        <div>
            {
                otherTopics.map(topic => (
                    <div class="timelineContainer pt-5">

                    <a href="#"><img class="img-fluid" src="../../static/assets/template/images/t1-sm.png"/></a>
                    <p class="textBelow colorWhite fontSize1vw">VẮC XIN PHÒNG BỆNH</p>
                </div>
                ))


            }
        </div>
    );
}

const Post = () => {
    const router = useRouter();
    const currPostName = router.query.postName;
    const currName = posts.filter(post => post.postName == currPostName)

    // how do I get the name of this post name?
    return (
        <Layout>
            <h3 class="colorDarkBlue fontSansation fontSize1-5vw pl-5 ml-5">{currName}</h3>
            <div class="container-fluid m-4">
                <div class="row">            
                    <div class="col-md-1">
                    </div>
                    <div class="col-md-6 py-2">

                    </div>

                    {/* <!-- for other topics --> */}
                    <div class="col-md-4 vl colScroll">
                        <div class="timelineContainer pt-5">
                            <h3 class="fontSize1-5vw">CÁC CHỦ ĐỀ BÀI VIẾT</h3>
                        </div>
                        <OtherTopic/>
                    </div>
                </div>
            </div>
            <OtherTopic posts={posts} currPostName={currPostName}/>
        </Layout>
    );
}
export default Post;
